import React, {useEffect, useState} from "react"
import Web3 from "web3";
import crowdFunding from "../crowdfunding";

function Navbar() { 
  const [target, settarget] = useState("");
  const [title, settitle] = useState("");
  const [content, setcontent] = useState("");
  const [address, setAddress] = useState(null);
  const [cfContract, setCfContract] = useState();
  const [CName, setCName] = useState([]);
  const [CDesc, setCDesc] = useState([]);
  const [CTarget, setCTarget] = useState([]);
  
  
  const stylename = {
    color: "black",
    fontSize: "30px",
    fontFamily: "Red Hat Display"
  };

  useEffect(() => {
    const web3 = new Web3(window.ethereum)
    const loadAccounts = async () => {
    if (typeof window !== "undefined" && typeof window.ethereum !== "undefined") {
    window.ethereum.request({ method: "eth_requestAccounts" })
    const accounts = await web3.eth.getAccounts()
    setAddress(accounts[0])
    var contract = crowdFunding(web3)
    setCfContract(contract)
    
  } else {
    console.log("Please Install Metamask");
  } 
  }
  loadAccounts()
  },[address, target, content, title])

  const setContractDetails = async (event) => {
    event.preventDefault();
    let CampaignName = title;
    let description = content;
    let reqFund = target;
    const details = await cfContract.methods.createCampaign(CampaignName, description, reqFund).send({
      from: address
    });
  }

  const getCampaigns = async () => {
    var name = await cfContract.methods.getCampaignName("1").call()
    var desc = await cfContract.methods.getCampaignDesc("0").call()
    var Target = await cfContract.methods.getCampaignTargetAmount("0").call()
    setCName(name)
    setCDesc(desc)
    setCTarget(Target)
    }
  

  function setTitle(event){
    settitle(event.target.value);
    }
  function setContent(event){
    setcontent(event.target.value);
    }
  function setTarget(event){
    settarget(event.target.value);
    }

  return (
    
    <div className="container-fluid">

      <section id="head">
        <nav className="navbar navbar-expand-lg navbar-white">
          <a className="navbar-brand" href="#">
            <img className="brandimg" src="./images/logo.png" alt="fundpk-Logo" />
            <span style={stylename}>FundGrants</span>
          </a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarTogglerDemo01" aria-controls="navbarTogglerDemo01" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarTogglerDemo01">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <a className="nav-link" href="">Home</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="">Discover</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#Create">Create</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="">About</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="">Contact</a>
              </li>
