      <section id="mid-start">
        <div className="row">
            <div className="col-lg-6">
                <h1>Discover and Fund <br/> Extraodionary Public Goods</h1>
                <h2 style={{marginTop: "3%"}}>Fund new and exciting Projects <br/> in a secure way!</h2>
        <div className="buttons-discover">
            <a className="btn-discover-1 btn btn-lg btn-primary d-block d-sm-inline" href="/grants/explorer/">View All Projects</a>
            <a className="btn-discover-2 btn btn-lg btn-outline-primary ml-sm-3 d-block d-sm-inline mt-2 mt-sm-0" href="/grants/new">Create Project</a>
        </div>  
        </div>
        <div className="col-lg-6">
