    <div className="title">Create Grant</div>
    <div className="content">
      <form>
        <div className="user-details">
          <div className="input-box">
            <span className="details">Title</span>
            <input onChange={setTitle} type="text" name="title"/>
          </div>
          <div className="input-box">
            <span className="details">Content</span>
            <textarea onChange={setContent} name="content" rows="4" cols="78"></textarea>
          </div>
          <div className="input-box">
            <span className="details">Project URL</span>
            <input type="text" />
          </div>
          <div className="input-box">
            <span className="details">Deadline</span>
            <input type="text"/>
          </div>
          <div className="input-box">
            <span className="details">Target</span>
            <input type="text" onChange={setTarget} name="target" required/>
          </div>
        </div>
