  </section>

  {CName.map((Item, index) => {
        return (
          <div style={{display: "inline-block"}}className="container-fluid">
          <div><h4>Project Title: {Item}</h4>
          <div><p>Project Details: {CDesc[index]}</p></div>
          <div><p>Target:{CTarget[index]}</p></div>
          <div><button>Fund</button></div>
          </div>
          </div>
        );
      })}
  </div>
  );
